CREATE TABLE #EMPLOYEE
(
EMP_ID INT,
FIRSTNAME VARCHAR(30),
LASTNAME VARCHAR(30),
MIDDLENAME VARCHAR(30),
JOBTITLE VARCHAR(50),
GROUPNAME VARCHAR(100),
MANAGER_ID INT
);


WITH DR (MANAGER_ID, MANAGER_FIRST_NAME, MANAGER_LAST_NAME, MANAGER_TITLE,
EMP_ID, EMP_FIRST_NAME, EMP_LAST_NAME, EMP_JOBTITLE, ORGLEVEL) AS
(
---- seed
SELECTMANAGER_ID, CAST(NULL AS VARCHAR(30)) AS MGR_FNAME, CAST(NULL AS VARCHAR(30)) AS MGR_LNAME, CAST(NULL AS VARCHAR(50)) AS MGR_TITLE,
EMP_ID, FIRSTNAME, LASTNAME, JOBTITLE, 0 AS ORGLEVEL
FROM #EMPLOYEE
WHEREMANAGER_ID = 0
UNION ALL
--- recursive add
SELECTE.MANAGER_ID, DR.EMP_FIRST_NAME AS MGR_FNAME, DR.EMP_LAST_NAME AS MGR_LNAME, DR.EMP_JOBTITLE AS MGR_TITLE,
E.EMP_ID, E.FIRSTNAME,E.LASTNAME,E.JOBTITLE, ORGLEVEL+1
FROM#EMPLOYEE AS E
INNER JOIN
DR
ON
E.MANAGER_ID = DR.EMP_ID
)
SELECT *
FROM DR
ORDER BY MANAGER_ID
;